import React from 'react';
import './reservation.scss';
import { Reservation } from '../../types/reservation';

interface ReservationProps {
    reservation: Reservation;
}

function ReservationItem(props: ReservationProps) {
    const { reservation } = props;
    return (
        <div className='reservation-card-container'>
            <div className='reservation-card-flex'>
                <div  className='id'>{reservation.id}</div> 
                <div className='status'>{reservation.status}</div>
            </div>
            <div className='reservation-card-flex'>
                <div className='area-shift-container'>
                   <div className='item-container'> <span className='label'>Area:</span> {reservation.area}</div>
                   <div className='shift'> <span className='label'>Shift:</span>   {reservation.shift}</div>
                </div>

                <div>
                    <div className='name'>
                        Customer Name: {reservation.customer.firstName} {reservation.customer.lastName}
                    </div>
                    <div className='date'>
                        Date: {reservation.businessDate}
                    </div>

                </div>
            </div>
            <div className='notes'>
               Notes: {reservation.guestNotes}
            </div>

        </div>
    );
}

export default ReservationItem;
