import React from 'react';
import './filter.scss';
import { Box, FormControl, InputLabel, Select, MenuItem, SelectChangeEvent } from '@mui/material';
import { areaFilter, shiftFilter, statusFilter } from '../../mocks/filters';

function Filter(props: any) {
    const [filter, setFilter] = React.useState('');

    const handleChange = (event: SelectChangeEvent) => {
        setFilter(event.target.value as string);
    };
    return (
        <Box sx={{ minWidth: 120 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Status:</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={filter}
                    label="Age"
                    onChange={handleChange}
                > {
                        statusFilter.map(sFilter => (
                            <MenuItem value={sFilter.value}>{sFilter.key}</MenuItem>
                        ))
                    }
                </Select>
            </FormControl>
            {/* <FormControl >
          <InputLabel id="demo-simple-select-label">Date:</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={filter}
            label="Age"
            onChange={handleChange}
          >
            <MenuItem value={'date'}>Date</MenuItem>
            <MenuItem value={'status'}>Status</MenuItem>
            <MenuItem value={'shift'}>Shift</MenuItem>
            <MenuItem value={'area'}>Area</MenuItem>
          </Select>
        </FormControl> */}
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Shift:</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={filter}
                    label="Age"
                    onChange={handleChange}
                >
                    {
                        shiftFilter.map(sFilter => (
                            <MenuItem value={sFilter.value}>{sFilter.key}</MenuItem>
                        ))
                    }
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Area:</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={filter}
                    label="Age"
                    onChange={handleChange}
                >

                    {
                        areaFilter.map(aFilter => (
                            <MenuItem value={aFilter.value}>{aFilter.key}</MenuItem>
                        ))
                    }

                </Select>
            </FormControl>
        </Box>
    );
}

export default Filter;
