import React from 'react';
import './filter.scss';
import { Box, FormControl, InputLabel, Select, MenuItem, SelectChangeEvent, TextField } from '@mui/material';
import { areaFilter, shiftFilter, statusFilter } from '../../mocks/filters';
import { DatePicker, LocalizationProvider } from '@mui/lab';
import AdapterDateFns from '@mui/lab/AdapterDateFns';

function Filter(props: any) {
    const [filter, setFilter] = React.useState('');
    const [value, setValue] = React.useState(null);

    const handleChange = (event: SelectChangeEvent) => {
        setFilter(event.target.value as string);
    };
    return (
        <Box sx={{ minWidth: 120 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Status:</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={filter}
                    label="Age"
                    onChange={handleChange}
                > {
                        statusFilter.map(sFilter => (
                            <MenuItem value={sFilter.value}>{sFilter.key}</MenuItem>
                        ))
                    }
                </Select>
            </FormControl>

      <DatePicker
        label="Helper text example"
        value={value}
        onChange={(newValue: any) => {
          setValue(newValue);
        }}
        renderInput={(params: any) => (
          <TextField {...params} helperText={params?.inputProps?.placeholder} />
        )}
      />

            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Shift:</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={filter}
                    label="Age"
                    onChange={handleChange}
                >
                    {
                        shiftFilter.map(sFilter => (
                            <MenuItem value={sFilter.value}>{sFilter.key}</MenuItem>
                        ))
                    }
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Area:</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={filter}
                    label="Age"
                    onChange={handleChange}
                >

                    {
                        areaFilter.map(aFilter => (
                            <MenuItem value={aFilter.value}>{aFilter.key}</MenuItem>
                        ))
                    }

                </Select>
            </FormControl>
        </Box>
    );
}

export default Filter;
